local function addHoverEffect(button, isMain, name)
	button.MouseEnter:Connect(function()
		-- Подсветка при наведении, если НЕ активна
		if (isMain and name ~= Page_1) or (not isMain and name ~= Page_1) then
			button.TextColor3 = Color3.fromRGB(255, 255, 255)
		end
	end)

	button.MouseLeave:Connect(function()
		-- Возврат в серый, если НЕ активна
		if (isMain and name ~= Page_1) or (not isMain and name ~= Page_1) then
			button.TextColor3 = Color3.fromRGB(173, 173, 173)
		end
	end)
end


