local subPages = {}
local currentMain = "rage"
local currentSub = "general"
local pageElementCounts = {}

for mainName, subs in pairs(subPagesConfig) do
	subPages[mainName] = {}
	for _, sub in ipairs(subs) do
		local f = Instance.new("Frame")
		f.Name = mainName .. "_" .. sub.name
		f.Size = UDim2.new(1, 0, 1, 0)
		f.Position = UDim2.new(0, 0, 0.12, 0)
		f.BackgroundTransparency = 1
		f.Visible = false
		f.Parent = MainFrame
		subPages[mainName][sub.name] = f
	end
end


local function updateSubButtonVisibility()

	for _, subs in pairs(subPagesConfig) do
		for _, sub in ipairs(subs) do
			sub.button.Visible = false
		end
	end

	
	for _, sub in ipairs(subPagesConfig[currentMain]) do
		sub.button.Visible = true
	end
end

local function updateSubButtonStyles()
	for _, subs in pairs(subPagesConfig) do
		for _, sub in ipairs(subs) do
			if currentMain == subs.main and sub.name == currentSub then
				sub.button.TextColor3 = Color3.new(1, 0.67, 0.14)
			else
				sub.button.TextColor3 = Color3.new(0.55, 0.55, 0.55)
			end
		end
	end
end

local function updateVisibility()
	
	updateSubButtonVisibility()


	for main, subs in pairs(subPages) do
		for sub, frame in pairs(subs) do
			frame.Visible = (main == currentMain and sub == currentSub)
		end
	end

	-- Update button styles
	updateSubButtonStyles()
end


