local verticalPadding = 30
local startY = 70
local elementTracker = {}

-- Функция для получения следующей позиции Y
local function getNextYPosition(mainPage, subPage, elementHeight)
	local key = mainPage .. "_" .. subPage
	elementTracker[key] = elementTracker[key] or {y = startY}

	local currentY = elementTracker[key].y
	elementTracker[key].y = currentY + elementHeight + verticalPadding

	return currentY
end


