	local function openDropdown()
		
		if currentOpenDropdown and currentOpenDropdown ~= dropdownFrame then
			local prevOptions = currentOpenDropdown:FindFirstChild("OptionsFrame")
			local prevOpenIndicator = currentOpenDropdown:FindFirstChild("Indicator_Open")
			local prevClosedIndicator = currentOpenDropdown:FindFirstChild("Indicator_Closed")
			local prevUIStroke = currentOpenDropdown:FindFirstChild("UIStroke")

			if prevOptions then prevOptions.Visible = false end
			if prevOpenIndicator then prevOpenIndicator.Visible = false end
			if prevClosedIndicator then prevClosedIndicator.Visible = true end
			if prevUIStroke then prevUIStroke.Color = Color3.new(0.188235, 0.188235, 0.188235) end
			currentOpenDropdown.ZIndex = 10
		end

		
		currentOpenDropdown = dropdownFrame
		optionsFrame.Visible = true
		indicatorOpen.Visible = true
		indicatorClosed.Visible = false
		UIStroke.Color = Color3.fromRGB(255, 171, 37)
		dropdownFrame.ZIndex = 15
	end

	
	for i, option in ipairs(options) do
		local optionButton = Instance.new("TextButton")
		optionButton.Name = "Option_"..i
		optionButton.Position = UDim2.new(0, 0, 0, (i-1)*25)
